import { ServerOptions, PluginOption } from 'vite';

declare type HttpsOption = ServerOptions['https'];
interface Config {
    assetHost?: string;
    assetsDir?: string;
    configPath?: string;
    publicDir?: string;
    mode?: string;
    entrypointsDir?: string;
    sourceCodeDir?: string;
    host?: string;
    https?: HttpsOption;
    port?: number;
    publicOutputDir?: string;
    watchAdditionalPaths?: string[];
}
interface PluginOptions {
    root?: string;
    outDir?: string;
    base?: string;
}
declare type UnifiedConfig = Config & PluginOptions;
declare type MultiEnvConfig = Record<string, Config | undefined>;
declare type Entrypoints = Array<[string, string]>;

declare const projectRoot: string;
declare function ViteRubyPlugin(): PluginOption[];

export default ViteRubyPlugin;
export { Config, Entrypoints, HttpsOption, MultiEnvConfig, PluginOptions, UnifiedConfig, projectRoot };
